INSERT INTO public.game (game_number,name,description,main_photo,is_continuously_delivered,is_sold_out,trailer_id) VALUES ('c1e017c0-5fa9-49ca-8326-c665ee5ec234', 'Fable II', 'This game supports English.  Download Fable II, the best-selling action role-playing game, in which you take on the role of a Hero in the fully realised land of Albion. Play through an epic story, become the world’s greatest fighter, gunslinger or mage. Explore dozens of open, unique environments with your faithful dog. Make friends, fall in love and start a family. Meet hundreds of characters and face some very tough decisions. In Fable II, everything you do has a consequence.  There are no refunds for this item. For more information, see www.xbox.com/live/accounts.', 'https://media.rawg.io/media/games/cec/cec82d526f9d056d426c985562963eec.jpg', true, false, NULL);
