INSERT INTO public.game (game_number,name,description,main_photo,is_continuously_delivered,is_sold_out,trailer_id) VALUES ('f720104a-9447-42ad-941f-eea3c3151932', 'Maneater', 'Maneater is a Single-Player Action RPG, set in the unforgiving waters of the Gulf Coast. Fight to survive in the open ocean, with danger lurking at every depth. Your only tools are your wits, your jaws, and an uncanny ability to evolve as you feed. Anything and everything is on the menu... kill or be killed.Become a MANEATER
