INSERT INTO public.game (game_number,name,description,main_photo,is_continuously_delivered,is_sold_out,trailer_id) VALUES ('a614cbe1-b223-4356-89aa-193d68ca189e', 'Far Cry 6', 'Welcome to Yara, a tropical paradise frozen in time. As the dictator of Yara, Anton Castillo is intent on restoring his nation back to its former glory by any means, with his son, Diego, following in his bloody footsteps. Their oppressive rule has ignited a revolution.

