INSERT INTO public.game (game_number,name,description,main_photo,is_continuously_delivered,is_sold_out,trailer_id) VALUES ('136d7588-b1e0-4111-8b1c-9b0aff4d9ad9', 'Dauntless', 'Dauntless is a free-to-play action role-playing video game developed by Phoenix Labs and published by Epic Games. The game initially launched in beta in May 2018 for Microsoft Windows. The game launched on May 21, 2019 for PlayStation 4, and Xbox One, including full support for cross-platform play.', 'https://media.rawg.io/media/games/82e/82e96c9d95f1be27c02a99962fdd16ec.jpg', true, false, NULL);
INSERT INTO public.game (game_number,name,description,main_photo,is_continuously_delivered,is_sold_out,trailer_id) VALUES ('5a9cc803-e2fe-4213-83ee-a538ad3cfb83', 'RobocraftX', 'Robocraft X is a new game built within the Robocraft Universe being developed from the ground up based on a new powerful online physics engine. It is at a very early stage of development at the moment which is why this Steam store page is Unlistedand only available to those who have the url shared with them.
